INSERT INTO `characters` VALUES (30,'Бесстрашный \"Страж\"','Эльфы','Паладин','Мифическая','эльф',3,4,3,1,1,2,2,0.15,1.2,'\"Мой Гнев\" Если поблизости в кресте 2х клеток есть враг получает +1 ед атаки','\"Взов\" Дует в рог и скрытые эльфы нанося урон из леса, стреляют и наносят 2(200) ед урона','\"Боль\"Забирает у союзника -1/-1 Физ.Защиты/Маг.Защиты и вешает их себе','\"Вой\" Призывает души всех старейшин и наделяет 3х союзников +1/+1 Физ.Защиты/Маг.Защиты','бесстрашный страж',3452),(31,'Вечный сраж','Эльфы','Кавалерия','Мифическая','эльф',3,5,3,1,1,2,2,0.3,1.4,'\"Вы мне никто\" Получает от всех юнитов на 10% меньше урона','\"Боль в груди\" Швыряет во врага копье и наносит ему 1.5(150) ед урона, и скорость падает на половину движения','\"Я ваш щит\" Ближайшего союзника за себя, и тот получает +1 Физ.защиты','\"Вы не умрете\" 2 выбраных союзника получают +1 очко здоровья','Вечный страж на скакуне',1423),(32,'Гаргулья','МагическиеСущества','Паладин','Обычная','магсущ',1,4,2,2,1,2,1,0.1,1.2,'\"Я хочу жить всё-таки\" -  если осталось меньше 30% хп, летят прочь на 3 клетки назад и окаменевают на 2 хода, после чего имеют + 1 к физ. защите (нельзя нанести физ. урон или как-либо взаимодействовать с картой, но карта становится препятсвием, как гора)','\"Дыхание огнём\" - изрыгает из своей пасти пламя, нанося  от 1 до 1.75 урона юниту (выбирается случайно), дальность 2 клетки','\"Мне надо отдохнуть\" - превращается в камень на 2 хода (нельзя взаимдоействовать с картой, но карта становится препятсвием, как гора), игнорирует любой физ. урон','\"Частичное окаменение\" - увеличивает физ. защиту до макс. на 3 хода ','гаргулья',5432),(33,'Дева на единороге','МагическиеСущества','Кавалерия','Обычная','магсущ',1,5,2,1,1,1,1,0.25,1.4,'\"Порядочная женщина\" - люди и гномы наносят на 30% меньше урона','\"Большой брат\" - призывает носорога (типа тоже один рог), который наносит  1 урона всем врагам на 3 клетки вперёд','\"Жаль такую красоту портить\" - враг не решается нанести физ. атаку данной красавице в течение 1 хода','\"Великое очарование\" - вдохновляет выбранного союзника и увеличивает ему силу атаки на 1 на 2 хода','Дева на единороге',1423),(34,'Дозорный','Гномы','Лучник','Обычная','гном',2,2,2,1,4,2,1,0.35,1.6,'\"Я рядом\"Союзный юнит в радиусе 1 клетки получает +1ед к атаке','\"Тяжелая рука\" достает 1 заряженный пистон и стреляет в радиусе 3х клеток по вертикале и диагонале нанося при этом 1(100)ед урона и разрушая врагу -1 Физ.Защиты','\"Я лучший\" получает +1/+1 Физ.Защиты/Маг.Защиты и увеличивает вер-ть крита на 10%','\"Повышение\" выбранный союзник получает +1 очко здоровья и +1/+1 Физ.Защиты и Маг.Защиты','Дозорный',53421),(35,'Защищеный ослик с телегой','Гномы','Кавалерия','Мифическая','гном',3,5,2,1,1,2,2,0.15,1.4,'\"Главное отступить\"Атакуя врага есть шанс в 30% отступить на одну клетку','\"Хлопнем их!\"Гном-Инженер в телеге заряжает мушкет и наносит врагам в радиусе 2х клеток по горизонтали и вертикали. Урон (2(200))','\"Пилюли для малышек\"Гном-Инженер кормит ослика специальными таблетками которые делают больше его показати урона +1 очко урона и Маг.Защиту на +1ед (действует 2 хода)','\"Все мы братья\" 1 юнит каждой фракции на поле получает +1/+1 Маг.Защиты и Физ.Защиты но не могут отступать на протяжении 2х ходов','Защищённый ослик с телегой',5342),(36,'Крестьянин с вилами','Люди','Паладин','Обычная','Чеел',1,4,1,1,1,1,1,0.2,1.2,'Проход через леса и поля осуществляется бесплатно (очки действий не тратяться)','Бросок грязью который наносит (2(200))','Могут возводить барикады ( из дерева хп - 1.5 (150))','по горизонтали дают союзным юнитам брони (+1(100))','Крестьянин с вилами',3452),(37,'Леди на пони','Эльфы','Кавалерия','Обычная','эльф',2,5,2,1,1,1,1,0.25,1.4,'\"Я голос правды\" Наносит на 10% больше урона лучникам  ','\"Вы слепые?!\" С размаху бьет мечом по врагу и наносит им 1(100) ед урона','\"Не убегать!\" союзники на одной линии получают +1 Физ.Защиты','\"Увечия\" Бьет союзника тот теряет -1 ед здоровья но получает +2/+2 Физ.Защиты/Маг.Защиты','Леди на коне',2354),(38,'Лесная братва','Эльфы','Лучник','Мифическая','эльф',3,2,2,1,4,2,1,0.35,1.6,'\"Оборона\" Если перед ним стоит непроходимый ландшафт получает  +1/+1 Физ.Защиты/Маг.Защиты','\"Ты моя цель\" Стреляет в врага самой острой стрелой тот получает на 5% больше урона и последующий противник тоже получает урон но уже на 20% меньше','\"Лес везде\" Союзник получает  +2/+1 Физ.Защиты/Маг.Защиты если рядом с ним в кресте в 1 клетке есть враг','\"Ты самый лучший!\" Юнит или союзник получает  +1/+2 Физ.Защиты/Маг.Защиты если он находится рядом с непроходимым ландшафтом','Лесная братва',534),(39,'Лучник тёмных эльфов','ТёмныеЭльфы','Лучник','Обычная','тэльф',1,2,2,1,4,2,1,0.2,1.6,'\"Ветеран перестрелок\" - наносит +20 урона по лучникам','\"Зоркий глаз\" - может нанести атаку дальностью на 6 клеток *урон как у обычной атаки','\"НЕВЕРОЯТНЫЙ СТРЕЛОК! КАК У НЕГО ЭТО ПОЛУЧАЕТСЯ?!\" -  отражает следующую атаку лучника, выстрелив своей стрелой в его стрелу','\"А вот бабушка моя была колдуньей\" - восстанавливает 50% хп себе','Лучник тёмных эльфов',12432),(40,'Магическая жаба','МагическиеСущества','Маг','Обычная','магсущ',1,3,1,2,3,2,1,0,0,'\"Друг животных\" - кавалерия наносит на 40% меньше урона','\"Снаряд пламени жабьего\" - плюет пламенем в любого выбранного противника в радиусе 3 клеток и наносит 1.5 урона','\"Ловушка жабьего рта\" - заточает выбранного противника в жабу на 2 хода (нельзя взаимодействовать с персонажем)','\"Режим отшельника\" - увеличивает здоровье, маг. и физ. защиту на 1 на 3 хода','Магическая жаба',123),(41,'Малой','ТёмныеЭльфы','Кавалерия','Обычная','эльфт',1,5,2,1,1,2,1,0.7,1.4,'\"Поддержка бати\" (работает, если в колоде есть карта \"Отец малого\" ) - увеличивает здоровье карты на 1 и маг защиту на 1 ','\"Чем больше шкаф, тем громче падает\" - наносит  2 урона всем, кроме гномов','\"Я всё папе расскажу\" - следующий удар наносит на 50% меньше урона','\"Отец бы мной гордился\" - повышает маг. и физ. защиту на 2 хода на 1','малой',423),(42,'Неумелый ученик','Гномы','Маг','Обычная','гном',1,3,1,2,3,1,2,0,0,'\"Я могу\" с шансом в 15% может атокавать не потратив очки действия','\"Лучший прием!\" влияет на мелкий металл рядом и формируя из него что-то вроде ножей и запускает их во врага 1(100)ед урона','\"Ты мой\" Собирает металл и формирует вокруг союзного юнита или себя что-то вроде железной девы и повышает его Физ.Защиту на +2ед (Действует 2 хода)','\"Вперед\" Двигает союзного юнита на одну клетку назади но дает ему +1ед скорости','Неумелый ученик',1312),(43,'Отец малого','ТёмныеЭльфы','Кавалерия','Мифическая','тэльф',3,5,3,1,1,2,2,0.05,1.4,'\"Поддержка сыночки\" -  ( работает, если в колоде есть карта\"Малой\" )  - увеличивает шанс крита до 40%','\"Папа может, папа может всё, что угодно\" -  вы кастуете магию, наносящую 2 урона с дальностью в 3 клетки','\"Ты че, маленький что-ли? Какая к черту магия?\" - полностью игнорирует любой магический урон на 2 хода','\"Сын бы мной гордился\" - повышает шанс крита до 30% на 3 хода','отец малого',423),(44,'Падший эльф','ТёмныеЭльфы','Паладин','Обычная','тймный',2,4,2,1,1,2,1,0.2,1.2,'\"Месть за прошлое\" - наносит увеличенный урон по эльфам на 20%','\"Причина изгнания\" - пользуясь запретной техникой высасывает из противника 1хп, восстанавливая себе 20% хп','\"Козырь в рукаве \" - в прямом смысле достаёт карту из рукава, увеличивает её, возводя стену на 2 хода','\"Всё ещё достоин! \" - пользуясь магией эльфов увеличивает маг. защиту на 1 перманенто','Падший эльф',543),(45,'Рекрут','Люди','Паладин','Обычная','Чеел',2,4,2,1,1,2,2,0.35,1.2,'увеличение показателя Маг.Защиты(2(200))','Взывание к свету наносит врагу урон (2(200))','В радиусе 1 клетки по горизонтали и вертикали дает союзным юнитам сопротивление магии','При смерти дает всем паладинам +1 очко здровья +1 урон ','рекрут',132),(46,'Самоучка','Гномы','Маг','Обычная','гном',2,3,1,2,3,1,1,0,0,'\"Будь сильнее\" наносит паладинам любой расы больше на 10% урона','\"Я тут новенький\" запускает во врага град из металлических шаров и нанося 1.5(150)ед урона','\"Создавай\" Обращает союзника в металлический купол и  тот  не может атаковать и так же по нему не проходит не один урон действует (1 ход)','\"Забирай\" Улучшает оружие одного юнита и увеличивает +1 ед урона','Самоучка',342),(47,'Старшина','Гномы','Лучник','Мифическая','гном',1,2,3,1,4,3,2,0.35,1.6,'\"Братья\" союзные гномы получают +1/+1 Физ.Защиты/Маг.Защиты в 2х клетка по вертикале и диагонале','\"Давай Два\"С помощью своей винтовки и пуль с наведением способен нанести урон двум целям которые стоят на соседних клетках или друг за другом урон второй цели будет увеличен на 15%','\"Как в былые временна\"Выпускает защитное облако газа, которые дает +1 Маг.Защиты','\"Давай!\" Выбранный союзник получает с шансом в 25% +2ед урона или с  50% шанс получить +1/+1 Физ.Защиты/Маг.Защиты','Старшина',1234),(48,'Старый мастер','Гномы','Паладин','Обычная','гном',1,4,2,1,1,2,1,0.25,1.2,'\"Старая битва\" урон против эльфов увеличен на +1','\"Бегите глупцы\" запугивает врагов, и те в страхе отступают на 1 клетку назад (если сзади непроходимое припятсвие отступает в одну из свободных клеток сбоку)','\"За наших\" все союзнуе юниты получают, +1 к атаке, но теряют -1 Физ.Защиты на 1 ход','\"Мы одна кровь\" случайные 2 союзника получают, 15% очков здоровья','Старый мастер',4323),(49,'Страж вольных заключенных','МагическиеСущества','Лучник','Обычная','сущмаг',2,2,2,1,4,2,1,0.15,1.6,'\"Заключение\" Враги когда атакуют этого юнита имеют шанс 20% промахнуться ','\"Ты мое сокровище\" Оковывает врага цепями и наносит 1.5(150) ед урона и замедляет его передвежение на 50% (действует 1 ход)','\"Все мы одно существо\" Все союзники типа Маг.Существа получают +1/+1 Физ.Защиты/Маг.Защиты','\"Оковы тьмы\" Союзник получает +1 ед атаки но в тоже время не может передвигаться','страж вольных заключенных',43),(50,'Ушедший в пустоту стрелок','МагическиеСущества','Лучник','Обычная','магсущ',1,2,2,1,4,1,2,0.22,1.6,'\"Пустота мой дом\" Может передвегаться назад не тратя очко хода','\"Детская травма\" натровляет на врага его страхи которые сводят его с ума и наносят урон 1.5(150) ед ','\"Моя память\" Достает последнюю вещь из мира живых и от старых воспоминаниях получает +1/+1 Физ.Защиты/Маг.Защиты','\"Старый враг\" волшебная стрела атакует врага и отнемает у него с шансом 50% либо Маг.Защиту/Физ.Защиту и наделяет ее случайного союзника ','Ушедший в пустоту стрелок',44);
